import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import MicRecorder from 'mic-recorder-to-mp3';


function CardItem(props) {
  const [comment, setComment] = useState(false);
  this.state = {
      isRecording: false,
      blobURL: '',
      isBlocked: false,
  };
  const Mp3Recorder = new MicRecorder({ bitRate: 128 });

  const handleClick = () => setComment(!comment);

  const start = () => {
    if (this.state.isBlocked) {
      console.log('Permission Denied');
    } else {
      Mp3Recorder
        .start()
        .then(() => {
          this.setState({ isRecording: true });
        }).catch((e) => console.error(e));
    }
  };

  const stop = () => {
    Mp3Recorder
      .stop()
      .getMp3()
      .then(([buffer, blob]) => {
       const blobURL = URL.createObjectURL(blob)
       this.setState({ blobURL, isRecording: false });
      }).catch((e) => console.log(e));
  };
  
  return (
    <>
      <li className='cards__item'>
        <figure className='cards__item__link' to={props.path}>
          <figure className='cards__item__pic-wrap' data-category={props.user}>
            <img
              className='cards__item__img'
              alt='username'
              src={props.src}
            />
          </figure>
              <div className='cards__item__info'>
                <h5 className='cards__item__text'>{props.text}</h5>
                <figure> {props.audio}</figure>
                <Link className="comment" onClick={handleClick}>
                    <p1>Comment <i class="fas fa-microphone-alt"></i> </p1> <br></br>
                </Link>

                <div className='comment' onClick={handleClick}>
                    {comment ?
                    <header className="App-header">
                        <button onClick={start} disabled={this.state.isRecording}>Record</button>
                        <button onClick={stop} disabled={!this.state.isRecording}>Stop</button>
                        <audio src={this.state.blobURL} controls="controls" />
                    </header>: ''}
                </div>

                <Link>
                    <p1>Share <i class="fas fa-share"></i> </p1>
                </Link>
              </div>
          </figure>
      </li>
    </>
  );
}

export default CardItem;
