import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import MicRecorder from 'mic-recorder-to-mp3';

function CardItem(props) {
  const Mp3Recorder = new MicRecorder({ bitRate: 128 });

  const [comment, setComment] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [blobURL, setBlobURL] = "";
  const [isBlocked, setIsBlocked] = useState(false);

  const handleClick = () => setComment(!comment);

  const start = () => {
    if (isBlocked) {
      console.log('Permission Denied');
    } else {
      Mp3Recorder
        .start()
        .then(() => {
            setIsRecording(true);
        }).catch((e) => console.error(e));
    }
  };

  const stop = () => {
    Mp3Recorder
      .stop()
      .getMp3()
      .then(([buffer, blob]) => {
       setBlobURL(URL.createObjectURL(blob));
       setIsRecording(false); 
      }).catch((e) => console.log(e));
  };

  navigator.getUserMedia({ audio: true },
  () => {
    console.log('Permission Granted');
    setIsBlocked(false);
  },
  () => {
    console.log('Permission Denied');
    setIsBlocked(true);
  },
  );
  
  return (
    <>
      <li className='cards__item'>
        <figure className='cards__item__link' to={props.path}>
          <figure className='cards__item__pic-wrap' data-category={props.user}>
            <img
              className='cards__item__img'
              alt='username'
              src={props.src}
            />
          </figure>
              <div className='cards__item__info'>
                <h5 className='cards__item__text'>{props.text}</h5>
                <figure> {props.audio}</figure>
                <Link className="comment" onClick={handleClick}>
                    <p1>Comment <i class="fas fa-microphone-alt"></i> </p1> <br></br>
                </Link>

                <div className='comment'>
                    {comment ?
                    <header className="comment-display">
                        <br></br>
                        <button onClick={start} disabled={isRecording}>Record</button>
                        <button onClick={stop} disabled={!isRecording}>Stop</button>
                        <audio src={blobURL} controls="controls" />
                    </header>: ''}
                </div>

                <Link>
                    <p1>Share <i class="fas fa-share"></i> </p1>
                </Link>
              </div>
          </figure>
      </li>
    </>
  );
}

export default CardItem;
